mapper.select:
select a.* , b.bannername from article a , banner b where a.bannerid = b.bannerid
and
articleid=#{articleid}

select a.* , b.bannername from article a , banner b where a.bannerid = b.bannerid

	and a.title = #{title}


	and a.bannerid = #{bannerid}


	and a.image = #{image}


	and a.istop = #{istop}


	and a.isflv = #{isflv}


	and a.contents = #{contents}


	and a.addtime = #{addtime}


	and a.hits = #{hits}
